mapper.select:

select * from reply
where bno = #{bno}
order by r_group asc, r_depth asc , rno desc

mapper.update:

update reply
set content = #{content}
where rno = #{rno}


update reply
set content = '(삭제된 댓글입니다.)'
where rno = #{rno}

mapper.select:

select count(rno)
from reply
where rno != #{rno} and r_group = #{rno}
